    componentDidMount(){
        const matchId = this.props.match.params.id
        this.setState(prevState =>{

        })
        this.props.dispatch(singleImageFetch(this.state.id))
    }

    render(){
        console.log('refresh')
        const singleImage = this.props.singleImage
        const {loading, error} = {...singleImage}

    if(loading){
        return <Loading />
    }

    if(error){
        return <Error error={error} />
    }

    return (
        <div className='image-details-wrapper'>
            <ImageCard image={singleImage} />
        </div>
    )

}
}

const mapPropToState = state =>{
    return {
        singleImage: state.singleImage
    }
}

export default withRouter(connect(mapPropToState)(DetailsScreen))
