import React from 'react'
import ImageCard from '../components/ImageCard'
import Header from '../components/HeaderSection'
import {connect} from 'react-redux'
import Loading from '../components/Loading'
import NoItem from '../components/NoItem'
import imageListThunk from '../redux/action/imageListActionThunk'


class Images extends React.Component{

    componentDidMount(){
        this.props.dispatch(imageListThunk())

    }

    render(){
        const {images, error, loading} = this.props.images
        const {imageUrl} = this.props.auth

        if(loading){
            return <Loading />
        }

        let errorOrImageCard = <NoItem />

        if(!error){
            err
        }

        return (
        <div>
            <section>
                <Header image={imageUrl}/>
            </section>
            <section>
                {images.map(image => <ImageCard image={image} />)}
            </section>
            </div>

        )
    }

}

const mapState = (state) => {
    return {
        auth: state.auth,
        images: state.images

    }
}

export default connect(mapState)(Images)
