import React from 'react'
import ImageCard from '../components/ImageCard'
import Header from '../components/HeaderSection'
import {connect} from 'react-redux'
import {withRouter} from 'react-router-dom'
import Loading from '../components/Loading'
import NoItem from '../components/NoItem'
import imageListThunk from '../redux/action/imageListActionThunk'
import thumpThunk from '../redux/action/thumpThunk'
import '../styles/imageList.css'



class Images extends React.Component{

    constructor(props){
        super(props)
        this.token = 
        this.handleLogin = this.props.handleLogin()
    }


    componentDidMount(){
        const authError = this.props.images.error
        this.props.dispatch(imageListThunk())

        if(authError === 'You are not authorized'){
            this.props.handleLogin()
        }
    }


    handleThumpUp = (imageId) =>{
        if(!this.token){
            this.handleLogin()
        }else{
            this.props.dispatch(thumpThunk(imageId, this.token))
        }
    }



    handleThumpDown(imageId, token=this.props.auth.token){
        if(!this.token){
            this.props.handleLogin()
        }else{
            this.props.dispatch(thumpThunk(imageId, token, false))
        }

    }

    render(){
        const {images, loading} = this.props.images
        const {imageUrl} = this.props.auth

        let noImageOrImageCard = <NoItem />

        if(images.length > 0){
            noImageOrImageCard = images.map(image => <ImageCard image={image} handleThumpUp={this.handleThumpUp} handleThumpDown={this.handleThumpDown}/>)
        }

        return (
        <div>
            <section className='card-session'>
                <Header />
                {loading ? <div className='loading'><Loading /></div> : noImageOrImageCard}
            </section>
            </div>

        )
    }

}

const mapState = (state) => {
    return {
        auth: state.auth,
        images: state.images

    }
}

export default withRouter(connect(mapState)(Images))
