import React from 'react'
import ImageCard from '../components/ImageCard'
import Header from '../components/HeaderSection'
import {connect} from 'react-redux'
import Loading from '../components/Loading'
import NoItem from '../components/NoItem'
import imageListThunk from '../redux/action/imageListActionThunk'


class Images extends React.Component{
    constructor(props){
        super(props)
    }

    componentDidMount(){
        //this.props.dispatch(imageListThunk())

    }

    render(){
        const images = this.props.images
        const {imageUrl, loading} = this.props.auth

        if(loading){
            return <Loading />
        }

        if(!images.length > 0){
            return <NoItem />
        }

        return (
        <div>
            <section>
                <Header image={imageUrl}/>
            </section>
            <section>
                {images.map(image => <ImageCard image={image} />)}
            </section>
            </div>

        )
    }

}

const mapState = (state) => {
    return {
        auth: state.auth,
        images: state.images

    }
}

export default connect(mapState)(ImageCard)
