import React from 'react'
import {useDispatch} from 'react-redux'
import {useFormik} from 'formik'
import * as Yup from 'yup'

const SignupForm = () => {

    const validate = values => {
        const errors = {}
        const imageExt = values.image.split('.')
        const ext = imageExt[imageExt.length]
        if(!values.image){
            errors.image = 'Image is required'
        }else if(values.image.type !== 'image/jpg' || values.image.type !== 'image/jpeg' || values.image.type !== 'image/png'){
            errors.image = 'Only png, jpg or jpeg image format is valid'
        }

        return errors
    }
    const formik = useFormik({
        initialValues: {
            password: '',
            username: '',
            image: null
        },
        validate,

        validationSchema: Yup.object({
            password: Yup.string()
                .min(5, 'Password must be at least 5 characters')
                .required('Password field is required'),
            username: Yup.string()
                .min(5, 'Username must be 5 characters at least')
                .required('Username is required'),
        }),
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2))
        }
    })

    return (
        <form onSubmit={formik.handleSubmit}>
            <label htmlFor='username'>Username</label>
            <input
                id='username'
                name='username'
                type='text'
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                values={formik.values.username}
            />
            {formik.touched.username && formik.errors.username ? (<div>{formik.errors.username}</div>): null}

            <label htmlFor='password'>password</label>
            <input
                id='password'
                name='password'
                type='password'
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
            />
            {formik.touched.password && formik.errors.password ? (
                <div>{formik.errors.password}</div>
            ): null} 
            <label htmlFor='image'>Image</label>
            <input
                id='image'
                name='image'
                type='file'
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
            />
            {formik.touched.image && formik.errors.image ? (
                <div>{formik.errors.image}</div>
            ): null}
            <button type='submit'>submit</button>
        </form>
    )
}

export default SignupForm
