import React from 'react'
import '../styles/imageCard.css'


const ImageCard = (props) =>{

    const image = props.image
    const userId = props.userId
    let thumpUpUser = false
    let thumpDownUser = false
    cons

    // check if user is authenticated to know if the user had thumped the image
    if(userId){
        // only thumped up image is looped
        if(image.thumpup.thumpUpCount > 0){
            let userObj
            for(userObj of image.thumpUp.userActionThumpUp){
                if(userObj === userId){
                    thumpUpUser = true
                    console.log(thumpUpUser)
                }

            }
        }

        if(image.thumpdown.thumpDownCount > 0){
            thumpDownUser = image.thumpdown.userActionThumpDown.find(id => id === userId)
        }
    }


    return(
            <div className='card-wrapper'>
                 <section className='creator'>
                    <div className='creator-image-wrapper'>
                        <img src={`http://localhost:5000/static/${image.creator.imageUrl}`} alt='creator'/> 
                    </div>
                    <div className='username'>{image.creator.username}</div>
                </section>

                <section className='image-url'>
                    <img src={`http://localhost:5000/static/${image.imageUrl}`} alt="share"/>
                </section>

                <section className='data-description'>
                    <div className='thump'>
                        <i className="far fa-thumbs-up thumpup-icon"></i>
                        <div className={'thump-count ' + thumpUpUser ? 'thumped' : null}>
                        {image.thumpUp.thumpUpCount}
                        </div>

                        <i class="far fa-thumbs-down thumpdown-icon"></i>
                        <div className={'thump-count ' + thumpDownUser ? 'thumped' : null}>
                        {image.thumpDown.thumpDownCount}
                        </div>

                    </div>


                    <div className='time-category'>
                        <span>{image.createdAt}</span>
                        <span>{image.category}</span>
                    </div>
                </section>
            </div>
        )
}

export default ImageCard
