import {START_FETCH_LIST, FAIL_FETCH_LIST, SUCCESS_FETCH_LIST} from '../action/imageListAction'

const initialState = {
    loading: false,
    images: [],
    error: ''
}

const imagesReducer = (state=initialState, action) => {
    switch(action.type){
        case START_FETCH_LIST:
            return {
                ...state,
                loading: true
            }
        case SUCCESS_FETCH_LIST:
            return{
                ...state,
                loading: false,
                images: action.payload.images.data
            }
        case FAIL_FETCH_LIST:
            return {
                ...state,
                error: action.payload.images.error
            }
        default:
            return state

    }
}
