import React, {useEffect} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {useParams} from 'react-router-dom'
import ImageCard from '../components/ImageCard'
import singleImageThunk from '../redux/action/singleImageThunk'
import singleImageFetch from '../redux/action/singleImageThunk'
import Loading from '../components/Loading'
import Error from '../components/Error'

const DetailsScreen = (props)=>{
    const singleImage = useSelector(state => state.singleImage)
    const {loading, error} = {...singleImage}
    const {id} = useParams()
    const dispatch = useDispatch()

    useEffect(()=>{
        dispatch(singleImageFetch(id))
    },[id])

    if(loading){
        return <Loading />
    }

    if(error)

    return (
        <div className='image-details-wrapper'>
            <ImageCard image={singleImage} />
        </div>
    )

}

export default DetailsScreen
