import React, { useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import Re from 'react-router-dom'
import {Formik} from 'formik'
import Loading from './Loading'
import '../styles/signup.css'
import uploadThunk from '../redux/action/uploadThunk'

const UploadImage = ({handleSignup}) => {
   const dispatch = useDispatch()
   const SingleData = useSelector(store => store.singleImage)
   const token = useSelector(store => store.auth.token)
   const {error, success, loading} = SingleData

    const validate = values => {
        const errors = {}

        if(!values.category){
                errors.category = 'Category is required'
            }else if(!values.description){
                errors.description = 'Password is required'
            }else if(!values.image){
                errors.image = 'Image is required'
            }else if(values.image){
                const imageExt = values.image.name.split('.')
                const ext = imageExt[imageExt.length-1]
                const supportedFormat = ['png', 'jpg', 'jpeg']
                if(!supportedFormat.includes(ext)){
                errors.image = "The uploaded image is not in a supported image format. Only png, jpg or jpeg"
                }
        }

        return errors
    }

    useEffect(() => {
        if(success){
            <Redirect to="/details" />
        }
    }, [success])

    return (
    <Formik
        initialValues = {{
            password: '',
            username: '',
            image: null
        }}
        validate = {validate}
        onSubmit= {values => dispatch(uploadThunk(values.image, values.description, values.category, token))}
        >

        {props => (
        <div className="form-wrapper">
        <div className="form_control">

            <div className='signup'>Upload Photo</div>

            <form onSubmit={props.handleSubmit}>
                <div className='username-wrapper'>
                    <i className="fa fa-user icon"></i>
                    <label>
                        Pick your favorite flavor:
                        <select name="category">
                            <option value="love">Love</option>
                            <option value="celebration">Celebration</option>
                            <option value="happiness">Happiness</option>
                            <option value="sadness">Sadness</option>
                            <option value='throwback'>ThrowBack</option>
                            <option value="general">General</option>
                        </select>
                        </label>

                <div className='form-error'>
                    {props.touched.category && props.errors.category ? 
                    (<div>{props.errors.category}</div>): null}
                </div>
                </div>

                <div className='password-wrapper'>
                    <label htmlFor='description' />
                   <textarea name="description">
                        image caption here...
                   </textarea>
                </div>
                <div className='form-error'>
                    {props.touched.description && props.errors.description ? (
                        <div>{props.errors.description}</div>
                    ): null}
                </div>

                <div className='image-wrapper'>
                    <i className="fas fa-upload icon"></i>
                    <label htmlFor='image' className='customed-upload'>
                    <input
                        id='image'
                        name='image'
                        type='file'
                        onChange={(event) =>{
                            props.setFieldValue("image", event.currentTarget.files[0]);
                        }}
                        onBlur={props.handleBlur}
                    />
                    <div className='upload-label'>Upload image</div>

                    </label>

                </div>
                <div className='form-error'>
                {props.touched.image && props.errors.image ? 
                props.errors.image : null }

                {props.values.image && (<div className='image-name'>{props.values.image.name}</div>)}
                </div>
                <div className='cancel-signup-wrapper'>
                    <div className='cancel-signup' onClick={handleSignup}>Cancel</div>
                    <button className='signup-submit' type='submit'>{loading ? <Loading /> : 'Submit'}</button>

                </div>
            </form>
            {error && (<div className='reducer-error'>{error}</div>)}
        </div>
    </div>
        )}
    </Formik>
    )
}

export default UploadImage
