import {createStore, combineReducers} from 'redux'
import appleMiddleware from 'redux-thunk'
import thunk from 'redux-thunk'
import {persistReducer, persistStore} from 'redux-persist'
import storage from 'redux-persist/lib/storage'
import {authReducer} from './reducer/authReducer'
import images from './reducer/images'
import singleImage from './reducer/singleImageReducer'


// the combine reducers
const reducers = combineReducers({
    auth: auth,
    images: images,
    singleImage: singleImage
})

const persistConfig = {
    key: 'root',
    //using localstorage
    storage,
    //only the auth reducer will be persisted
    whitelist: ['auth']
}

const persistedReducer = persistReducer(persistConfig, reducers)

export default () => {
    let store = createStore(persistedReducer, appleMiddleware(thunk))
    let persistor = persistStore(store)
    return {store, persistor}


}
