import React, {useEffect} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {useParams} from 'react-router-dom'
import ImageCard from '../components/ImageCard'
import singleImageThunk from '../redux/action/singleImageThunk'
import singleImageFetch from '../redux/action/singleImageThunk'

const DetailsScreen = (props)=>{
    const singleImage = useSelector(store => store.singleImage)
    const {id} = useParams()
    const dispatch = useDispatch()

    useEffect(()=>{
        dispatch(singleImageFetch(id))
    },)

    return (
        <div className='image-details-wrapper'>
            <ImageCard image={singleImage} />
        </div>
    )

}

export default DetailsScreen
