import React, { useState } from 'react'
import Images from '../src/screen/ImageList'
import {Provider} from 'react-redux'
import {PersistGate} from 'redux-persist/integration/react'
import {store} from './redux/store'
import {useStore} from 'react-redux'
import Loading from './components/Loading'
import NoItem from './components/NoItem'
import imageListThunk from './redux/action/imageListActionThunk'
import Header from './components/HeaderSection'
import Signup from './components/signupModal'
import SignupForm from './components/signupModal'
import ImageDetails from './screen/ImageDetails'
import './styles/App.css'
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import ImageList from '../src/screen/ImageList'



function App() {

  const [openLoginModel, setOpenLogininModel] = useState(false)
  const [openSignupModel, setOpenSignupModel] = useState(false)

  const authData = store.getState().auth
  const {auth, imageUrl} = authData

  const handleLoginModel = () =>{
    setOpenSignupModel(false)
    setOpenLogininModel(true)
  }

  const handleSignupModel = () =>{
    if(openSignupModel){
      setOpenSignupModel(false)

    }else{setOpenSignupModel(true)}
  }



  let loginAndSignup = (<div className='logout-and-signin'>
    <ul>
    <li className="login nav-box" onClick={handleLoginModel}>Login</li>
    <li className="sign-up nav-box" onClick={handleSignupModel}>Signup</li>
    </ul>
  </div>)


  //const store = storeConfig().store
  //const persistor = storeConfig().persistor
  return (
    <Provider store={store}>

      <nav className='nav-wrapper'>
          <div className='website-name'>gidiShare</div>
          {auth ? <div className="logout nav-box">Logout</div> : loginAndSignup}
      </nav>

      <div className='main-body'>
      <section className={(openSignupModel ? 'signup-modal-open' : 'signup-modal-close')}>
        <SignupForm handleSignup={handleSignupModel}/>
      </section>

      <Switch>
        <Route path='/images' exact component={ImageList} />
        <Route path='/details' exact component={ImageDetails} />
        <Route path='/'/>
      </Switch>

      </div>
  </Provider>
  )
}

export default App;
