import React, { useEffect } from 'react'
import {useDispatch} from 'react-redux'
import {useFormik} from 'formik'
import * as Yup from 'yup'
import {useState} from 'react'

import '../styles/signup.css'

const SignupForm = ({openSignup}) => {
    const [imageName, setImageName] = useState(null)
    const [close, setClose] = useState(false)

    useEffect(()=>{console.log(openSignup)}, [])

    const handleClose = () =>{
        setClose(true)
    }

    const validate = values => {
        const errors = {}


        if(!values.image){
            errors.image = 'Image is required'
        }else if(values.image){
            // set the name of the image here because of the customed file input
            const splitImagePath = values.image.split('\\')
            // values.image is split because validator often add fake path to the image
            setImageName(splitImagePath[splitImagePath.length-1])
            const imageExt = values.image.split('.')
            const ext = imageExt[imageExt.length-1]
            const supportedFormat = ['png', 'jpg', 'jpeg']
            if(!supportedFormat.includes(ext)){
            errors.image = "The uploaded image is not in a supported image format"
            }
        }

        return errors
    }
    const formik = useFormik({
        initialValues: {
            password: '',
            username: '',
            image: null
        },
        validate,

        validationSchema: Yup.object({
            password: Yup.string()
                .min(5, 'Password must be at least 5 characters')
                .required('Password field is required'),
            username: Yup.string()
                .min(5, 'Username must be 5 characters at least')
                .required('Username is required'),
        }),
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2))
        }
    })

    return (

        <div className={"form-wrapper "  + (openSignup ? 'modal-open ' : 'modal_close ') + (close )}>
        <div className="form_control">
            <div className="close-sign" onClick={handleClose}>&times;</div>

            <div className='signup'>SIGNUP</div>

            <form onSubmit={formik.handleSubmit}>
                <div className='username-wrapper'>
                    <label htmlFor='username' />
                    <i class="fa fa-user icon"></i>
                    <input
                        id='username'
                        name='username'
                        type='text'
                        placeholder='Username'
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        values={formik.values.username}
                    />
                </div>
                <div className='form-error'>
                    {formik.touched.username && formik.errors.username ? 
                    (<div>{formik.errors.username}</div>): null}
                </div>

                <div className='password-wrapper'>
                    <label htmlFor='password' />
                    <i className="fa fa-key icon"></i>
                    <input
                        id='password'
                        name='password'
                        type='password'
                        placeholder='Password'
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />

                </div>
                <div className='form-error'>
                    {formik.touched.password && formik.errors.password ? (
                        <div>{formik.errors.password}</div>
                    ): null}
                </div>

                <div className='image-wrapper'>
                    <i className="fas fa-upload icon"></i>
                    <label htmlFor='image' className='customed-upload'>
                    <input
                        id='image'
                        name='image'
                        type='file'
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                    />
                    <div className='upload-label'>Upload image</div>

                    </label>

                </div>
                <div className='form-error'>
                {formik.touched.image && formik.errors.image ? (
                        <div >{formik.errors.image}</div>
                    ): <div>{imageName}</div>}
                </div>


                <button className='signup-submit' type='submit'>submit</button>
            </form>
        </div>
    </div>
    )
}

export default SignupForm
