import React, { useState } from 'react'
import {Provider} from 'react-redux'
import {PersistGate} from 'redux-persist/integration/react'
import {store} from './redux/store'
import {useStore} from 'react-redux'
import SignupForm from './components/signupModal'
import ImageDetails from './screen/ImageDetails'
import LoginForm from './components/Login'
import './styles/App.css'
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import ImageList from '../src/screen/ImageList'



function App() {

  const [openLoginModal, setOpenLogininModal] = useState(false)
  const [openSignupModal, setOpenSignupModal] = useState(false)

  const authData = store.getState().auth
  const {auth, error, success, loading} = authData

  use


  const handleLoginModal = () =>{
    if(openLoginModal){
      setOpenLogininModal(false)
    }else{setOpenLogininModal(true)}
  }

  const handleSignupModal = () =>{
    if(openSignupModal){
      setOpenSignupModal(false)

    }else{setOpenSignupModal(true)}
  }



  let loginAndSignup = (<div className='logout-and-signin'>
    <ul>
    <li className="login nav-box" onClick={handleLoginModal}>Login</li>
    <li className="sign-up nav-box" onClick={handleSignupModal}>Signup</li>
    </ul>
  </div>)


  //const store = storeConfig().store
  //const persistor = storeConfig().persistor
  return (
    <Provider store={store}>

      <nav className='nav-wrapper'>
          <div className='website-name'>gidiShare</div>
          {auth ? <div className="logout nav-box">Logout</div> : loginAndSignup}
      </nav>

      <div className='main-body'>
      <section className={(openSignupModal ? 'signup-modal-open' : 'signup-modal-close')}>
        <SignupForm handleSignup={handleSignupModal} reducerError={error} fetchSuccess={success} fetchLoading={loading}/>
      </section>

      <section className={(openLoginModal ? 'login-modal-open' : 'login-modal-close')}>
        <LoginForm handleLogin={handleLoginModal} reducerError={error} loading={loading} fetchSuccess={success}/>
      </section>

      <Switch>
        <Route path='/images' exact component={ImageList} />
        <Route path='/details' exact component={ImageDetails} />
        <Route path='/' component={ImageList}/>
      </Switch>
      </div>
  </Provider>
  )
}

export default App;
